using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace Problem8
{
    class Program
    {
        //Find the greatest product of five consecutive digits in the 1000-digit number.
        static void Main(string[] args)
        {
            //Here I use a lambda expression to put each number in a list, then I iterate throug this list to find the answer
            string nr = "";
            List<double> num = new List<double>();
            nr.ToList().ForEach(v => num.Add(char.GetNumericValue(v)));
           
            double biggestProduct = 0;
            for (int i = 4; i < 1000; i++)
            {
                double thisProduct = num[i - 4] * num[i - 3] * num[i - 2] * num[i - 1] * num[i];

                if (thisProduct > biggestProduct)
                    biggestProduct = thisProduct;
            }
            Console.WriteLine("{0}", biggestProduct);

            Console.ReadKey();
        }
    }
}
